import React, { useState, useEffect } from "react";

const Product = ({ id, title, description, price, image, category }) => {
  const [message, setMessage] = useState("");
  const [messageDetalle, setMessageDetalle] = useState("");

  const handleBuy = () => {
    setMessage("Gracias por su compra");
  };

  const handleDetalle = () => {
    setMessageDetalle("Este es el detalle");
  };

  return (
    <div>
      <h3>{id}</h3>
      <h2>{title}</h2>  
      <p>Precio: {price}</p>
      <p>{description}</p>
      <p>imagen: {image}</p>
      <p>Categoria: {category}</p>
      <button onClick={handleBuy}>Comprar</button>
      {message && <p>{message}</p>}
      <br />
      <button onClick={handleDetalle}>Detalle</button>
      {messageDetalle && <p>{messageDetalle}</p>}
    </div>
  );
};

const ProductDetails = () => {
  const [productos, setProductos] = useState([]);

  useEffect(() => {
    fetch("https://fakestoreapi.com/products/")
      .then((response) => response.json())
      .then((data) => setProductos(data))
      .catch((error) => console.log(error));
  }, []);

  return (
    <div>
      <h1>Detalles del producto</h1>
      {productos.map((producto) => (
        <Product
          key={producto.id}
          id={producto.id}
          title={producto.title}
          description={producto.description}
          category={producto.category}
          price={producto.price}
          image={producto.image}
        />
      ))}
    </div>
  );
};

export default ProductDetails;
